mapper.select:
select id,parent_id,group_id,name,namespace,version,ext_name,uri,creator_id,creator_name,create_time,update_time,updator_id,updator_name,description,type,share_type,status from apaas_file where
status = 1

    and parent_id = #{folderId} 


    and id = #{id}


    and id in

        #{id}



    and namespace in

        #{namespace}



    and ext_name = #{extName}


    and type = #{type}

order by update_time desc

select id,parent_id,group_id,name,namespace,version,ext_name,uri,creator_id,creator_name,create_time,update_time,updator_id,updator_name,description,type,share_type,status, icon from apaas_file where share_type = #{shareType} and
ext_name = #{extName} and
status = 1 order by update_time desc limit #{limit} offset #{offset}

select *
from apaas_file where
id = #{id}
and status in

    #{item}

select icon
from apaas_file where
id = #{id}
and status in

    #{item}

mapper.update:
update apaas_file
set
status = -1,
update_time = #{update_time},

    updator_name = #{updatorName},

updator_id = #{updatorId}

    where id = #{id}


    where parent_id = #{parentId}


    where group_id = #{groupId}

update apaas_file set name = #{name},
description = #{description},
update_time = #{update_time}, updator_id = #{updatorId}, updator_name = #{updatorName} where id = #{id}

update apaas_file
set
type = #{type},
update_time = #{update_time}
where id = #{id}

update apaas_file
set
delivery_channel = #{deliveryChannel},
update_time = #{updateTime}
where id = #{id}

mapper.select:
select count(*)  as total
from apaas_file
where
status = 1
and ext_name = #{extName}
and creator_name = #{userId}

mapper.update:
update apaas_file

    parent_id = 

        when id = #{item.id} then #{item.parentId}

    group_id = 

        when id = #{item.id} then #{item.groupId}

    update_time = 

        when id = #{item.id} then #{item.updateTime}


WHERE

    id = #{item.id}

update apaas_file set share_type = #{shareType}, updator_id = #{updatorId},
updator_name = #{updatorName},
update_time = #{updateTime} where id = #{id}

mapper.select:
select id,parent_id,group_id,name,namespace,version,ext_name,uri,creator_id,creator_name,create_time,update_time,updator_id,updator_name,description,type,share_type,status from apaas_file
where id 

    and id = #{id}


    and type = #{type}


    and ext_name = #{extName}


    and status = #{status}





        and group_id in

            #{groupId}




            and group_id = #{groupId}


            and group_id is null




    and 

        name like #{name}


        name = #{name}



    and creator_id = #{creatorId}


    and ref = #{ref}

order by update_time desc

select file.ext_name as "extName", count(file.id) as "count" from file where file.status = 1 and file.ext_name <> '' and file.ext_name <> 'folder' and file.ext_name <> 'tk'
and DATE_FORMAT(FROM_UNIXTIME(file.create_time / 1000), "%Y-%m-%d") >= #{startTime}
and DATE_FORMAT(FROM_UNIXTIME(file.create_time / 1000), "%Y-%m-%d") <= #{endTime}
and file.ext_name in
#{extName}
group by file.ext_name

mapper.update:
update apaas_file set update_time = #{updateTime}, updator_id = #{updatorId},
updator_name = #{updatorName},
status = 1 where id = ${id}

mapper.select:
select id,parent_id,group_id,name,namespace,version,ext_name,uri,creator_id,creator_name,create_time,update_time,updator_id,updator_name,description,type,share_type,status from apaas_file
where DATE_FORMAT(FROM_UNIXTIME(file.create_time / 1000), "%Y-%m-%d") >= #{startTime}
and DATE_FORMAT(FROM_UNIXTIME(file.create_time / 1000), "%Y-%m-%d") <= #{endTime}
and ext_name != 'folder'

    and ext_name = #{extName}

and status = 1
limit ${(pageIndex - 1)*pageSize}, #{pageSize}

select count(*) as "total" from apaas_file
where DATE_FORMAT(FROM_UNIXTIME(file.create_time / 1000), "%Y-%m-%d") >= #{startTime}
and DATE_FORMAT(FROM_UNIXTIME(file.create_time / 1000), "%Y-%m-%d") <= #{endTime}
and ext_name != 'folder'

    and ext_name = #{extName}

and status = 1

select apaas_file_pub.* from apaas_file_pub, apaas_file where
apaas_file.status = 1
and apaas_file.id = apaas_file_pub.file_id
and apaas_file.id = #{projectId}
and apaas_file_pub.id in (
    select max(id) from apaas_file_pub where module_id is not null group by module_id
)
order by apaas_file_pub.id desc;

select apaas_file.id, apaas_file.creator_id, apaas_user.name as creator_name, apaas_file.group_id, apaas_file.parent_id, apaas_file.name, apaas_file.ext_name, apaas_file.create_time, apaas_file.update_time, apaas_file.description, apaas_file.status, apaas_file.icon, apaas_file.share_type from apaas_file left JOIN apaas_user ON apaas_file.creator_id = apaas_user.id where apaas_file.creator_id = #{userId} and apaas_file.status = #{status} and apaas_file.group_id is null
and apaas_file.parent_id = #{parentId}
and apaas_file.parent_id is null
and apaas_file.ext_name in
#{extName}
order by apaas_file.id desc

select apaas_file.id, apaas_file.creator_id, apaas_user.name as creator_name, apaas_file.group_id, apaas_file.parent_id, apaas_file.name, apaas_file.ext_name, apaas_file.create_time, apaas_file.update_time, apaas_file.description, apaas_file.status, apaas_file.icon, apaas_file.share_type from apaas_file left JOIN apaas_user ON apaas_file.creator_id = apaas_user.id where apaas_file.status = #{status}
and apaas_file.group_id = #{groupId}
and 1
and apaas_file.parent_id = #{parentId}
and apaas_file.parent_id is null
and apaas_file.ext_name in
#{extName}
order by apaas_file.id desc

select id from apaas_file 
where name = #{name}
and ext_name = #{extName}


        and parent_id = #{parentId}


        and parent_id is null




        and group_id = #{groupId}


        and group_id is null



    and status = #{status}


    and creator_id = #{creatorId}

limit 1

select id,creator_id,creator_name,group_id,parent_id,name,ext_name,create_time,update_time,description,status,icon from apaas_file where parent_id = #{id} and status = 1
and ext_name in
#{extName}

SELECT * FROM (
SELECT A.id,A.name,A.creator_id,A.creator_name,A.update_time,A.ext_name,A.group_id,A.parent_id,A.share_type,null FROM apaas_file AS A
JOIN apaas_user_group_relation AS B ON IF(
    A.group_id is NULL,
    A.creator_id = #{userId},
    A.group_id = B.user_group_id AND B.user_id = #{userId} AND B.status = 1
) OR A.share_type = 1

UNION

SELECT C.id,C.name,C.creator_id,C.creator_name,C.update_time,'group',null,null,null,C.icon FROM apaas_user_group AS C
    JOIN apaas_user_group_relation AS D ON C.id = D.user_group_id AND D.user_id = #{userId} AND D.status = 1
) A WHERE name LIKE #{name} ORDER BY update_time DESC

    LIMIT #{limit}


    OFFSET #{offset}

select id,creator_id,creator_name,group_id,parent_id,name,ext_name,create_time,update_time,description,status,icon from apaas_file
where parent_id = ${id}
and status = 1

   and ext_name in

       #{extName}


order by create_time desc
