mapper.select:
select id, file_id,version,creator_id, create_time, update_time
from apaas_file_content
where
file_id = #{fileId}
order by update_time desc
limit 1;

select * from apaas_file_content where 1 = 1
and file_id = #{fileId}
and id in
#{id}
order by update_time
desc
asc
limit #{limit}

select id, file_id, creator_id, creator_name, create_time, update_time, status, content, content_type from apaas_file_content where status = 1 and
id in
#{id}

mapper.update:
update apaas_file_content
set
content = #{content},
update_time = #{updateTime}
where id = #{id}
